import React, { useState, useEffect } from 'react';

const Goals = () => {
    const [goals, setGoals] = useState([]); // State to store goals
    const [formData, setFormData] = useState({
        title: "",
        calories: "",
        start_date: "",
        end_date: "",
        status: "New",
    });

    // Fetch Goals from API on Component Mount
    useEffect(() => {
        fetch("http://127.0.0.1:3000/goals")
            .then((response) => response.json())
            .then((data) => setGoals(data))
            .catch((error) => console.error("Error fetching goals:", error));
    }, []);

    // Handle Input Change
    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    // Handle Form Submission to Create New Goal
    const handleSubmit = (e) => {
        e.preventDefault();
        fetch("http://127.0.0.1:3000/goals", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(formData),
        })
            .then((response) => response.json())
            .then((newGoal) => {
                setGoals([...goals, newGoal]); // Add the new goal to state
                setFormData({ title: "", calories: "", start_date: "", end_date: "", status: "New" });
            })
            .catch((error) => console.error("Error creating goal:", error));
    };

    // Filter Goals into In Progress and Complete
    const inProgressGoals = goals.filter(
        (goal) => goal.status === "New" || goal.status === "In Progress"
    );
    const completeGoals = goals.filter((goal) => goal.status === "Complete");

    return (
        <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
            <h1 style={{ textAlign: "center" }}>My Goals</h1>

            {/* New Goal Form */}
            <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
                <h2>New Goal</h2>
                <div>
                    <input
                        name="title"
                        placeholder="Title"
                        value={formData.title}
                        onChange={handleChange}
                        required
                        style={{ margin: "5px", padding: "5px" }}
                    />
                    <input
                        name="calories"
                        placeholder="Calories"
                        type="number"
                        value={formData.calories}
                        onChange={handleChange}
                        required
                        style={{ margin: "5px", padding: "5px" }}
                    />
                    <input
                        name="start_date"
                        placeholder="Start Date"
                        type="date"
                        value={formData.start_date}
                        onChange={handleChange}
                        required
                        style={{ margin: "5px", padding: "5px" }}
                    />
                    <input
                        name="end_date"
                        placeholder="End Date"
                        type="date"
                        value={formData.end_date}
                        onChange={handleChange}
                        required
                        style={{ margin: "5px", padding: "5px" }}
                    />
                    <button type="submit" style={{ margin: "5px", padding: "5px" }}>
                        Add Goal
                    </button>
                </div>
            </form>

            <div style={{ display: "flex", justifyContent: "space-around" }}>
                {/* In Progress Goals */}
                <div style={{ width: "45%" }}>
                    <h2>In Progress</h2>
                    {inProgressGoals.length === 0 ? (
                        <p>No goals in progress.</p>
                    ) : (
                        inProgressGoals.map((goal) => (
                            <div
                                key={goal.id}
                                style={{
                                    border: "1px solid #ccc",
                                    padding: "10px",
                                    marginBottom: "10px",
                                    borderRadius: "5px",
                                }}
                            >
                                <h3>
                                    <strong>{goal.title}</strong> - {goal.status}
                                </h3>
                                <p>Calories: {goal.calories}</p>
                                <p>
                                    Start: {goal.start_date} | End: {goal.end_date}
                                </p>
                            </div>
                        ))
                    )}
                </div>

                {/* Complete Goals */}
                <div style={{ width: "45%" }}>
                    <h2>Complete</h2>
                    {completeGoals.length === 0 ? (
                        <p>No completed goals.</p>
                    ) : (
                        completeGoals.map((goal) => (
                            <div
                                key={goal.id}
                                style={{
                                    border: "1px solid #ccc",
                                    padding: "10px",
                                    marginBottom: "10px",
                                    borderRadius: "5px",
                                    backgroundColor: "#f0f8ff",
                                }}
                            >
                                <h3>
                                    <strong>{goal.title}</strong> - {goal.status}
                                </h3>
                                <p>Calories: {goal.calories}</p>
                                <p>
                                    Start: {goal.start_date} | End: {goal.end_date}
                                </p>
                            </div>
                        ))
                    )}
                </div>
            </div>
        </div>
    );
};

export default Goals;
