import React, { useState, useEffect } from "react";

const Goals = () => {
    const [goals, setGoals] = useState([]); // State to store goals
    const [formData, setFormData] = useState({
        id: null, // For editing an existing goal
        title: "",
        calories: "",
        start_date: "",
        end_date: "",
        status: "New",
    });

    const [isEditing, setIsEditing] = useState(false); // Track if editing mode is active

    // Fetch Goals from API on Component Mount
    useEffect(() => {
        fetch("http://127.0.0.1:3000/goals")
            .then((response) => response.json())
            .then((data) => setGoals(data))
            .catch((error) => console.error("Error fetching goals:", error));
    }, []);

    // Handle Input Change
    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    // Handle Form Submission to Create/Update Goal
    const handleSubmit = (e) => {
        e.preventDefault();

        if (isEditing) {
            // Update existing goal
            fetch(`http://127.0.0.1:3000/goals/${formData.id}`, {
                method: "PATCH",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(formData),
            })
                .then((response) => response.json())
                .then((updatedGoal) => {
                    setGoals(goals.map((goal) => (goal.id === updatedGoal.id ? updatedGoal : goal)));
                    resetForm();
                })
                .catch((error) => console.error("Error updating goal:", error));
        } else {
            // Create new goal
            fetch("http://127.0.0.1:3000/goals", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(formData),
            })
                .then((response) => response.json())
                .then((newGoal) => {
                    setGoals([...goals, newGoal]);
                    resetForm();
                })
                .catch((error) => console.error("Error creating goal:", error));
        }
    };

    // Delete Goal
    const deleteGoal = (id) => {
        fetch(`http://127.0.0.1:3000/goals/${id}`, {
            method: "DELETE",
        })
            .then(() => {
                setGoals(goals.filter((goal) => goal.id !== id));
            })
            .catch((error) => console.error("Error deleting goal:", error));
    };

    // Edit Goal
    const editGoal = (goal) => {
        setFormData({ ...goal });
        setIsEditing(true);
    };

    // Move Goal to Next Status
    const moveToNextStatus = (goal) => {
        const newStatus =
            goal.status === "New" ? "In Progress" : goal.status === "In Progress" ? "Complete" : "Complete";

        fetch(`http://127.0.0.1:3000/goals/${goal.id}`, {
            method: "PATCH",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ status: newStatus }),
        })
            .then((response) => response.json())
            .then((updatedGoal) => {
                setGoals(goals.map((g) => (g.id === updatedGoal.id ? updatedGoal : g)));
            })
            .catch((error) => console.error("Error updating goal:", error));
    };

    // Reset Form
    const resetForm = () => {
        setFormData({ id: null, title: "", calories: "", start_date: "", end_date: "", status: "New" });
        setIsEditing(false);
    };

    // Filter Goals by Status
    const newGoals = goals.filter((goal) => goal.status === "New");
    const inProgressGoals = goals.filter((goal) => goal.status === "In Progress");
    const completeGoals = goals.filter((goal) => goal.status === "Complete");

    return (
        <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
            <h1 style={{ textAlign: "center" }}>My Goals</h1>

            {/* New/Edit Goal Form */}
            <form onSubmit={handleSubmit} style={{ marginBottom: "20px", textAlign: "center" }}>
                <h2>{isEditing ? "Edit Goal" : "New Goal"}</h2>
                <input
                    name="title"
                    placeholder="Title"
                    value={formData.title}
                    onChange={handleChange}
                    required
                    style={{ margin: "5px", padding: "5px" }}
                />
                <input
                    name="calories"
                    placeholder="Calories"
                    type="number"
                    value={formData.calories}
                    onChange={handleChange}
                    required
                    style={{ margin: "5px", padding: "5px" }}
                />
                <input
                    name="start_date"
                    type="date"
                    value={formData.start_date}
                    onChange={handleChange}
                    required
                    style={{ margin: "5px", padding: "5px" }}
                />
                <input
                    name="end_date"
                    type="date"
                    value={formData.end_date}
                    onChange={handleChange}
                    required
                    style={{ margin: "5px", padding: "5px" }}
                />
                <button type="submit" style={{ margin: "5px", padding: "5px" }}>
                    {isEditing ? "Update Goal" : "Add Goal"}
                </button>
                {isEditing && (
                    <button type="button" onClick={resetForm} style={{ margin: "5px", padding: "5px" }}>
                        Cancel
                    </button>
                )}
            </form>

            {/* Goals Section */}
            <div style={{ display: "flex", justifyContent: "space-between" }}>
                {/* New Goals */}
                <div style={{ width: "30%" }}>
                    <h2>New Goals</h2>
                    {newGoals.map((goal) => (
                        <GoalCard
                            key={goal.id}
                            goal={goal}
                            moveToNextStatus={moveToNextStatus}
                            deleteGoal={deleteGoal}
                            editGoal={editGoal}
                            buttonLabel="Move to In Progress"
                        />
                    ))}
                </div>

                {/* In Progress */}
                <div style={{ width: "30%" }}>
                    <h2>In Progress</h2>
                    {inProgressGoals.map((goal) => (
                        <GoalCard
                            key={goal.id}
                            goal={goal}
                            moveToNextStatus={moveToNextStatus}
                            deleteGoal={deleteGoal}
                            editGoal={editGoal}
                            buttonLabel="Move to Complete"
                        />
                    ))}
                </div>

                {/* Complete */}
                <div style={{ width: "30%" }}>
                    <h2>Complete</h2>
                    {completeGoals.map((goal) => (
                        <GoalCard
                            key={goal.id}
                            goal={goal}
                            deleteGoal={deleteGoal}
                            editGoal={editGoal}
                            showMoveButton={false}
                        />
                    ))}
                </div>
            </div>
        </div>
    );
};

const GoalCard = ({ goal, moveToNextStatus, deleteGoal, editGoal, buttonLabel = "", showMoveButton = true }) => (
    <div style={{ border: "1px solid #ccc", margin: "10px", padding: "10px", borderRadius: "5px" }}>
        <h3>{goal.title}</h3>
        <p>Calories: {goal.calories}</p>
        <p>
            Start: {goal.start_date} | End: {goal.end_date}
        </p>
        {showMoveButton && <button onClick={() => moveToNextStatus(goal)}>{buttonLabel}</button>}
        <button onClick={() => editGoal(goal)} style={{ margin: "5px" }}>
            Edit
        </button>
        <button onClick={() => deleteGoal(goal.id)} style={{ margin: "5px", color: "red" }}>
            Delete
        </button>
    </div>
);

export default Goals;
