import React, { useState, useEffect } from 'react';
import { getGoals, deleteGoal, updateGoalStatus } from '../services/api';

const Goals = () => {
    const [goals, setGoals] = useState([]);

    useEffect(() => {
        fetchGoals();
    }, []);

    const fetchGoals = async () => {
        const response = await getGoals();
        setGoals(response.data);
    };

    const handleDelete = async (id) => {
        await deleteGoal(id);
        fetchGoals();
    };

    const handleStatusChange = async (id, status) => {
        await updateGoalStatus(id, status);
        fetchGoals();
    };

    return (
        <div>
            <h1>My Goals</h1>
            {['New Goal', 'In Progress', 'Complete'].map((status) => (
                <div key={status}>
                    <h2>{status}</h2>
                    {goals
                        .filter((goal) => goal.status === status)
                        .map((goal) => (
                            <div key={goal.id}>
                                <p>
                                    Calories: {goal.target_calories} | Start: {goal.start_date} | End: {goal.end_date}
                                </p>
                                <button onClick={() => handleStatusChange(goal.id, 'In Progress')}>
                                    Move to In Progress
                                </button>
                                <button onClick={() => handleStatusChange(goal.id, 'Complete')}>
                                    Move to Complete
                                </button>
                                <button onClick={() => handleDelete(goal.id)}>Delete</button>
                            </div>
                        ))}
                </div>
            ))}
        </div>
    );
};

export default Goals;
