import React, { useEffect, useState } from 'react';

const Goals = () => {
    const [goals, setGoals] = useState([]);

    useEffect(() => {
        fetch('http://127.0.0.1:3000/goals') // Adjust the backend URL if needed
            .then((response) => response.json())
            .then((data) => setGoals(data))
            .catch((error) => console.error('Error fetching goals:', error));
    }, []);

    return (
        <div>
            <h1>My Goals</h1>
            <ul>
                {goals.map((goal) => (
                    <li key={goal.id}>{goal.target_calories} calories - Status: {goal.status}</li>
                ))}
            </ul>
        </div>
    );
};

export default Goals;
