import axios from 'axios';

// Set up the API base URL
const api = axios.create({
    baseURL: process.env.REACT_APP_API_URL || 'http://127.0.0.1:3000',
});

// User Authentication
export const signUp = (data) => api.post('/users', data);
export const signIn = (data) => api.post('/users/sign_in', data);

// Goals CRUD
export const getGoals = () => api.get('/goals');
export const createGoal = (goalData) => api.post('/goals', { goal: goalData });
export const updateGoal = (id, goalData) => api.put(`/goals/${id}`, { goal: goalData });
export const deleteGoal = (id) => api.delete(`/goals/${id}`);
export const updateGoalStatus = (id, status) =>
    api.patch(`/goals/${id}`, { goal: { status } });
