import React, { useState, useEffect } from "react";

const Goals = () => {
    const [goals, setGoals] = useState([]); // State to store goals
    const [formData, setFormData] = useState({
        title: "",
        calories: "",
        start_date: "",
        end_date: "",
        status: "New",
    });

    // Fetch Goals from API on Component Mount
    useEffect(() => {
        fetch("http://127.0.0.1:3000/goals")
            .then((response) => response.json())
            .then((data) => setGoals(data))
            .catch((error) => console.error("Error fetching goals:", error));
    }, []);

    // Handle Input Change
    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    // Handle Form Submission to Create New Goal
    const handleSubmit = (e) => {
        e.preventDefault();
        fetch("http://127.0.0.1:3000/goals", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(formData),
        })
            .then((response) => response.json())
            .then((newGoal) => {
                setGoals([...goals, newGoal]); // Add the new goal to state
                setFormData({ title: "", calories: "", start_date: "", end_date: "", status: "New" });
            })
            .catch((error) => console.error("Error creating goal:", error));
    };

    // Handle Moving Goal to Next Status
    const moveToNextStatus = (goal) => {
        const newStatus =
            goal.status === "New" ? "In Progress" : goal.status === "In Progress" ? "Complete" : "Complete";

        fetch(`http://127.0.0.1:3000/goals/${goal.id}`, {
            method: "PATCH",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ status: newStatus }),
        })
            .then((response) => response.json())
            .then((updatedGoal) => {
                setGoals(
                    goals.map((g) => (g.id === updatedGoal.id ? updatedGoal : g)) // Update the state
                );
            })
            .catch((error) => console.error("Error updating goal:", error));
    };

    // Filter Goals by Status
    const newGoals = goals.filter((goal) => goal.status === "New");
    const inProgressGoals = goals.filter((goal) => goal.status === "In Progress");
    const completeGoals = goals.filter((goal) => goal.status === "Complete");

    return (
        <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
            <h1 style={{ textAlign: "center" }}>My Goals</h1>

            {/* New Goal Form */}
            <form onSubmit={handleSubmit} style={{ marginBottom: "20px", textAlign: "center" }}>
                <h2>New Goal</h2>
                <input
                    name="title"
                    placeholder="Title"
                    value={formData.title}
                    onChange={handleChange}
                    required
                    style={{ margin: "5px", padding: "5px" }}
                />
                <input
                    name="calories"
                    placeholder="Calories"
                    type="number"
                    value={formData.calories}
                    onChange={handleChange}
                    required
                    style={{ margin: "5px", padding: "5px" }}
                />
                <input
                    name="start_date"
                    type="date"
                    value={formData.start_date}
                    onChange={handleChange}
                    required
                    style={{ margin: "5px", padding: "5px" }}
                />
                <input
                    name="end_date"
                    type="date"
                    value={formData.end_date}
                    onChange={handleChange}
                    required
                    style={{ margin: "5px", padding: "5px" }}
                />
                <button type="submit" style={{ margin: "5px", padding: "5px" }}>
                    Add Goal
                </button>
            </form>

            {/* Goals Section */}
            <div style={{ display: "flex", justifyContent: "space-between" }}>
                {/* New Goals */}
                <div style={{ width: "30%" }}>
                    <h2 style={{ textAlign: "center" }}>New Goals</h2>
                    {newGoals.map((goal) => (
                        <div
                            key={goal.id}
                            style={{
                                border: "1px solid #ccc",
                                padding: "10px",
                                margin: "10px 0",
                                borderRadius: "5px",
                            }}
                        >
                            <h3>{goal.title}</h3>
                            <p>Calories: {goal.calories}</p>
                            <p>
                                Start: {goal.start_date} | End: {goal.end_date}
                            </p>
                            <button onClick={() => moveToNextStatus(goal)}>Move to In Progress</button>
                        </div>
                    ))}
                </div>

                {/* In Progress */}
                <div style={{ width: "30%" }}>
                    <h2 style={{ textAlign: "center" }}>In Progress</h2>
                    {inProgressGoals.map((goal) => (
                        <div
                            key={goal.id}
                            style={{
                                border: "1px solid #ccc",
                                padding: "10px",
                                margin: "10px 0",
                                borderRadius: "5px",
                            }}
                        >
                            <h3>{goal.title}</h3>
                            <p>Calories: {goal.calories}</p>
                            <p>
                                Start: {goal.start_date} | End: {goal.end_date}
                            </p>
                            <button onClick={() => moveToNextStatus(goal)}>Move to Complete</button>
                        </div>
                    ))}
                </div>

                {/* Complete */}
                <div style={{ width: "30%" }}>
                    <h2 style={{ textAlign: "center" }}>Complete</h2>
                    {completeGoals.map((goal) => (
                        <div
                            key={goal.id}
                            style={{
                                border: "1px solid #ccc",
                                padding: "10px",
                                margin: "10px 0",
                                borderRadius: "5px",
                                backgroundColor: "#f0f8ff",
                            }}
                        >
                            <h3>{goal.title}</h3>
                            <p>Calories: {goal.calories}</p>
                            <p>
                                Start: {goal.start_date} | End: {goal.end_date}
                            </p>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Goals;
